import React, { useState, useEffect } from 'react';
import { TextField, Button, Box, Typography } from '@mui/material';
import axios from 'axios';

const SupplierForm = ({ supplierId, onSupplierUpdated }) => {
  const [name, setName] = useState('');
  const [contactInfo, setContactInfo] = useState('');
  const [productsSupplied, setProductsSupplied] = useState([]);
  const [newProduct, setNewProduct] = useState('');

  useEffect(() => {
    if (supplierId) {
      fetchSupplierDetails(supplierId);
    }
  }, [supplierId]);

  const fetchSupplierDetails = async (id) => {
    try {
      const response = await axios.get(`http://localhost:9001/api/suppliers/${id}`);
      const supplier = response.data.data;
      setName(supplier.name);
      setContactInfo(supplier.contact_info);
      setProductsSupplied(supplier.productsSupplied || []);
    } catch (error) {
      console.error('Error fetching supplier details:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const supplierData = {
      name,
      contact_info: contactInfo,
      productsSupplied,
    };

    try {
      if (supplierId) {
        // Update supplier
        await axios.put(`http://localhost:9001/api/suppliers/updateSupplier?id=${supplierId}`, supplierData);
      } else {
        // Create supplier
        await axios.post('http://localhost:9001/api/suppliers/createSupplier', supplierData);
      }
      onSupplierUpdated();
      clearForm();
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleAddProduct = () => {
    if (newProduct.trim() !== '') {
      setProductsSupplied([...productsSupplied, newProduct.trim()]);
      setNewProduct('');
    }
  };

  const clearForm = () => {
    setName('');
    setContactInfo('');
    setProductsSupplied([]);
    setNewProduct('');
  };

  return (
    <Box component="form" onSubmit={handleSubmit}>
      <Typography variant="h6">{supplierId ? 'Edit Supplier' : 'Add Supplier'}</Typography>
      
      <TextField
        label="Supplier Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        fullWidth
        margin="normal"
        required
      />

      <TextField
        label="Contact Info"
        value={contactInfo}
        onChange={(e) => setContactInfo(e.target.value)}
        fullWidth
        margin="normal"
        required
      />

      <Typography variant="body1">Products Supplied</Typography>
      <Box display="flex" alignItems="center" gap={2} marginBottom={2}>
        <TextField
          label="Product Name or SKU"
          value={newProduct}
          onChange={(e) => setNewProduct(e.target.value)}
          fullWidth
        />
        <Button variant="contained" onClick={handleAddProduct}>
          Add Product
        </Button>
      </Box>

      <Box>
        {productsSupplied.length > 0 && (
          <Typography variant="body2">Products Added:</Typography>
        )}
        {productsSupplied.map((product, index) => (
          <Typography key={index} variant="body2">
            {index + 1}. {product}
          </Typography>
        ))}
      </Box>

      <Box marginTop={2}>
        <Button type="submit" variant="contained" color="primary">
          {supplierId ? 'Update Supplier' : 'Create Supplier'}
        </Button>
        <Button variant="outlined" color="secondary" onClick={clearForm} style={{ marginLeft: '10px' }}>
          Clear
        </Button>
      </Box>
    </Box>
  );
};

export default SupplierForm;
