import React, { useState, useEffect } from 'react';
import { Container, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography } from '@mui/material';
import axios from 'axios';
import SupplierForm from './SupplierForm';

const SupplierList = () => {
  const [suppliers, setSuppliers] = useState([]);
  const [editSupplierId, setEditSupplierId] = useState(null);
  const [isFormVisible, setIsFormVisible] = useState(false);

  useEffect(() => {
    fetchSuppliers();
  }, []);

  const fetchSuppliers = async () => {
    try {
      const response = await axios.get('http://localhost:9001/api/suppliers/getAllSuppliers');
      setSuppliers(response.data.data); // Assuming the data is under 'data'
    } catch (error) {
      console.error('Error fetching suppliers:', error);
    }
  };

  const handleEditClick = (supplierId) => {
    setEditSupplierId(supplierId);
    setIsFormVisible(true);
  };

  const handleDeleteClick = async (supplierId) => {
    try {
      await axios.delete(`http://localhost:9001/api/suppliers/deleteSupplier?id=${supplierId}`);
      fetchSuppliers(); // Refresh the list after deletion
    } catch (error) {
      console.error('Error deleting supplier:', error);
    }
  };

  const handleFormClose = () => {
    setEditSupplierId(null);
    setIsFormVisible(false);
    fetchSuppliers(); // Refresh the list after create/update
  };

  return (
    <Container>
      <Typography variant="h4">Suppliers List</Typography>
      <Button variant="contained" color="primary" onClick={() => setIsFormVisible(!isFormVisible)}>
        {isFormVisible ? 'Hide Form' : 'Add Supplier'}
      </Button>
      
      {isFormVisible && (
        <SupplierForm supplierId={editSupplierId} onSupplierUpdated={handleFormClose} />
      )}

      <TableContainer component={Paper} style={{ marginTop: '20px' }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Supplier Name</TableCell>
              <TableCell>Contact Information</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {suppliers.map((supplier) => (
              <TableRow key={supplier._id}>
                <TableCell>{supplier.name}</TableCell>
                <TableCell>{supplier.contact_info}</TableCell>
                <TableCell>
                  <Button
                    variant="contained"
                    color="secondary"
                    onClick={() => handleEditClick(supplier._id)}
                    style={{ marginRight: '10px' }}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="contained"
                    color="error"
                    onClick={() => handleDeleteClick(supplier._id)}
                  >
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default SupplierList;
