import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Container, Typography } from '@mui/material';

const SignupPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [error, setError] = useState('');

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/users/register', { username, password, role });
      // Redirect to login page or show a success message
      window.location.href = '/login';
    } catch (err) {
      setError('Signup failed. Please try again.');
    }
  };

  return (
    <Container>
      <Typography variant="h2">Sign Up</Typography>
      {error && <Typography color="error">{error}</Typography>}
      <form onSubmit={handleSignup}>
        <TextField label="Username" value={username} onChange={(e) => setUsername(e.target.value)} required fullWidth />
        <TextField label="role" type="role" value={role} onChange={(e) => setRole(e.target.value)} required fullWidth />
        <TextField label="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required fullWidth />
        <Button type="submit" variant="contained" color="primary">Sign Up</Button>
      </form>
    </Container>
  );
};

export default SignupPage;
