import React, { useState } from 'react';
import { TextField, Button, Container, Typography } from '@mui/material';
import axios from 'axios';

const OrderForm = ({ setOrders }) => {
  const [productId, setProductId] = useState('');
  const [quantity, setQuantity] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:9001/api/orders', {
        product_id: productId,
        quantity: parseInt(quantity),
        status,
      });
      setOrders(prev => [...prev, response.data]);
      setProductId('');
      setQuantity('');
      setStatus('');
    } catch (error) {
      console.error('Error adding order', error);
    }
  };

  return (
    <Container>
      <Typography variant="h4">Add Order</Typography>
      <form onSubmit={handleSubmit}>
        <TextField label="Product ID" value={productId} onChange={(e) => setProductId(e.target.value)} required fullWidth />
        <TextField label="Quantity" type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} required fullWidth />
        <TextField label="Status" value={status} onChange={(e) => setStatus(e.target.value)} required fullWidth />
        <Button type="submit" variant="contained" color="primary">Add Order</Button>
      </form>
    </Container>
  );
};

export default OrderForm;
