import React, { useState, useEffect } from 'react';
import SupplierForm from '../components/SupplierManagement/SupplierForm';
import SupplierList from '../components/SupplierManagement/SupplierList';
import { Container, Grid } from '@mui/material';
import axios from 'axios';

const SuppliersPage = () => {
  const [suppliers, setSuppliers] = useState([]);
  const [editSupplierId, setEditSupplierId] = useState(null);

  // Fetch suppliers on page load
  useEffect(() => {
    fetchSuppliers();
  }, []);

  const fetchSuppliers = async () => {
    try {
      const response = await axios.get('http://localhost:9001/api/suppliers/getAllSuppliers');
      setSuppliers(response.data.data); // Assuming data comes under 'data'
    } catch (error) {
      console.error('Error fetching suppliers:', error);
    }
  };

  const handleSupplierUpdated = () => {
    setEditSupplierId(null); // Reset edit mode
    fetchSuppliers(); // Refresh the supplier list
  };

  const handleEditSupplier = (supplierId) => {
    setEditSupplierId(supplierId);
  };

  return (
    <Container>
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <SupplierForm
            supplierId={editSupplierId}
            onSupplierUpdated={handleSupplierUpdated}
          />
        </Grid>
        <Grid item xs={12} md={6}>
          <SupplierList
            suppliers={suppliers}
            onEditSupplier={handleEditSupplier}
            onSupplierDeleted={fetchSuppliers}
          />
        </Grid>
      </Grid>
    </Container>
  );
};

export default SuppliersPage;
